$(".image input[type=file]").change(function(){if(0!=this.files.length){var b=$(".image form"),c=new FormData(b[0]);c.append("file","image"),$.ajax({url:location.href,type:"POST",data:c,enctype:"multipart/form-data",processData:!1,contentType:!1,success:function(a){"success"!=a&&console.log(a)},error:function(a){console.log(a)}})}});