page_script({init:function(){$.fn.editable.defaults.ajaxOptions={type:"POST"};$.fn.editable.defaults.type="text";$.fn.editable.defaults.pk=1;$.fn.editable.defaults.mode="inline";$.fn.editable.defaults.inputclass="form-control";$.fn.editable.defaults.url=location.origin+"/account/set_user_attrib";$.fn.editable.defaults.onblur="submit";$.fn.editable.defaults.emptytext="Vide";$("#displayname").editable({name:"displayname",success:function(b,c){$(".top-menu .user-btn .username").text(c);$(".profile-usertitle-name").text(c)}});$("#email").editable({name:"email"});$("#mobile").editable({name:"mobile"});var a=function(){$(".success_msg").hide();$(".new_password_min_length_error").hide();$(".old_password_error").hide();$(".unhandled_error").hide()};$("#login_form input[name=oldpassword]").change(a);$("#login_form input[name=newpassword]").change(a);$("#password_form").ajaxForm({beforeSubmit:a,success:function(b){try{parsed=JSON.parse(b);if(parsed.status=="success"){$(".success_msg").show();$("#password_form")[0].reset()}else{if(parsed.status=="new_password_min_length_error"){$(".new_password_min_length_error").show();$("#login_form input[name=newpassword]").val("");$("#login_form input[name=newpassword]").focus()}else{if(parsed.status=="not_logged_in"){app.logout(location.origin+"/login",true)}else{if(parsed.status=="old_password_error"){$(".old_password_error .remaining_attempts").text(parsed.params.remaining_attempts);$(".old_password_error").show();$("#login_form input[name=oldpassword]").val("");$("#login_form input[name=oldpassword]").focus()}else{console.log(b);$(".unhandled_error").show()}}}}}catch(c){console.log(b);$(".unhandled_error").show()}}});$(".mark_as_read").click(function(){$.post(location.href,{pages_seen:true},function(b){try{parsed=JSON.parse(b);if(parsed.status=="success"){$(".last_insertions_list").empty()}else{console.log(b)}}catch(c){console.log(b)}})})}});